import axios from 'axios';

// Configurar la base URL de tu API Quarkus
const API_BASE_URL = 'http://localhost:3030/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Tipos de datos (ajusta según tu backend)
export interface Paciente {
  id?: number;
  nombre: string;
  apellido: string;
  dni: string;
  telefono: string;
  email: string;
  fechaNacimiento: string;
}

export interface Medico {
  nombreMedico: string;
  totalCitas: number;
  canceladas: number;
  atendidas: number;
  programadas: number;
}

export interface Cita {
  id?: number;
  pacienteId: number;
  doctorId: number;
  fecha: string;
  hora: string;
  motivo: string;
  estado: 'PROGRAMADA' | 'COMPLETADA' | 'CANCELADA';
}

// Servicios para Pacientes
export const pacienteService = {
  getAll: () => api.get<Paciente[]>('/pacientes'),
  getById: (id: number) => api.get<Paciente>(`/pacientes/${id}`),
  create: (paciente: Paciente) => api.post<Paciente>('/pacientes', paciente),
  update: (id: number, paciente: Paciente) => api.put<Paciente>(`/pacientes/${id}`, paciente),
  delete: (id: number) => api.delete(`/pacientes/${id}`),
};

// Servicios para Doctores
export const medicoService = {
  getAll: () => api.get<Medico[]>('/medicos'),
  getById: (id: number) => api.get<Medico>(`/medicos/${id}`),
  create: (medico: Medico) => api.post<Medico>('/medicos', medico),
  update: (id: number, medico: Medico) => api.put<Medico>(`/medicos/${id}`, medico),
  delete: (id: number) => api.delete(`/medicos/${id}`),
};

// Servicios para Citas
export const citaService = {
  getAll: () => api.get<Cita[]>('/citas'),
  getById: (id: number) => api.get<Cita>(`/citas/${id}`),
  create: (cita: Cita) => api.post<Cita>('/citas', cita),
  update: (id: number, cita: Cita) => api.put<Cita>(`/citas/${id}`, cita),
  delete: (id: number) => api.delete(`/citas/${id}`),
};

export default api;
